import React, { useState } from 'react';
import './App.css';

function App() {
  const [step, setStep] = useState('login'); // login ‚Üí profile ‚Üí bmi
  const [user, setUser] = useState({ 
    email: '', password: '', 
    name: '', age: '', gender: '', height: '', weight: '' 
  });
  const [bmi, setBmi] = useState(null);
  const [status, setStatus] = useState('');

  // Demo login credentials: email: admin@example.com, password: 123456
  const handleLogin = (e) => {
    e.preventDefault();
    if (user.email === 'admin@example.com' && user.password === '123456') {
      setStep('profile');
    } else {
      alert('Invalid credentials! Use: admin@example.com / 123456');
    }
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    if (user.name && user.age && user.gender && user.height && user.weight) {
      setStep('bmi');
    }
  };

  const calculateBMI = () => {
    const heightInMeters = parseFloat(user.height) / 100;
    const bmiValue = (parseFloat(user.weight) / (heightInMeters * heightInMeters)).toFixed(2);

    setBmi(bmiValue);

    if (bmiValue < 18.5) setStatus('Underweight');
    else if (bmiValue < 25) setStatus('Normal');
    else if (bmiValue < 30) setStatus('Overweight');
    else setStatus('Obese');
  };

  const handleInputChange = (field, value) => {
    setUser(prev => ({ ...prev, [field]: value }));
  };

  const logout = () => {
    setStep('login');
    setUser({ email: '', password: '', name: '', age: '', gender: '', height: '', weight: '' });
    setBmi(null);
    setStatus('');
  };

  if (step === 'login') {
    return (
      <div className="app">
        <div className="container">
          <h1>üîê BMI Calculator - Sign In</h1>
          <form onSubmit={handleLogin} className="auth-form">
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={user.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="admin@example.com"
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={user.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="123456"
                required
              />
            </div>
            <button type="submit" className="auth-btn">Sign In</button>
            <p className="demo-info">Demo: admin@example.com / 123456</p>
          </form>
        </div>
      </div>
    );
  }

  if (step === 'profile') {
    return (
      <div className="app">
        <div className="container">
          <div className="header">
            <h1>Welcome, {user.name || 'User'}!</h1>
            <button onClick={logout} className="logout-btn">Logout</button>
          </div>

          <h2>üë§ Personal Details</h2>
          <form onSubmit={handleProfileSubmit} className="profile-form">
            <div className="form-row">
              <div className="form-group">
                <label>Full Name</label>
                <input
                  type="text"
                  value={user.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="Enter your name"
                  required
                />
              </div>
              <div className="form-group">
                <label>Age</label>
                <input
                  type="number"
                  value={user.age}
                  onChange={(e) => handleInputChange('age', e.target.value)}
                  placeholder="Years"
                  min="1"
                  max="120"
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>Gender</label>
              <select
                value={user.gender}
                onChange={(e) => handleInputChange('gender', e.target.value)}
                required
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Height (cm)</label>
                <input
                  type="number"
                  value={user.height}
                  onChange={(e) => handleInputChange('height', e.target.value)}
                  placeholder="170"
                  step="0.1"
                  required
                />
              </div>
              <div className="form-group">
                <label>Weight (kg)</label>
                <input
                  type="number"
                  value={user.weight}
                  onChange={(e) => handleInputChange('weight', e.target.value)}
                  placeholder="70"
                  step="0.1"
                  required
                />
              </div>
            </div>

            <button type="submit" className="next-btn">Calculate BMI</button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>üìä {user.name}'s BMI Result</h1>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>

        <div className="result-container">
          <div className="user-info">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Age:</strong> {user.age} years</p>
            <p><strong>Gender:</strong> {user.gender}</p>
          </div>

          <div className="bmi-result">
            <h2>Your BMI: <span>{bmi}</span></h2>
            <p className={`status ${status.toLowerCase()}`}>{status}</p>
          </div>

          <div className="actions">
            <button onClick={() => setStep('profile')} className="recalc-btn">
              Recalculate
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
