import React, { useState } from 'react';
import './App.css';

function App() {
  const [step, setStep] = useState('signup'); // signup ‚Üí login ‚Üí profile ‚Üí bmi
  const [user, setUser] = useState({ 
    name: '', email: '', password: '', 
    age: '', gender: '', height: '', weight: '' 
  });
  const [users, setUsers] = useState([]); // Store registered users
  const [bmi, setBmi] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');

  // Sign up new user
  const handleSignup = (e) => {
    e.preventDefault();
    if (user.name && user.email && user.password) {
      // Check if user already exists
      if (users.find(u => u.email === user.email)) {
        setError('User already exists! Please sign in.');
        return;
      }

      // Save new user
      setUsers([...users, { name: user.name, email: user.email, password: user.password }]);
      setError('');
      setStep('login');
      alert('Sign up successful! Please sign in.');
    }
  };

  // Sign in existing user
  const handleLogin = (e) => {
    e.preventDefault();
    const foundUser = users.find(u => u.email === user.email && u.password === user.password);

    if (foundUser) {
      setUser(prev => ({ ...prev, name: foundUser.name }));
      setStep('profile');
      setError('');
    } else {
      setError('Invalid credentials!');
    }
  };

  const handleProfileSubmit = (e) => {
    e.preventDefault();
    if (user.age && user.gender && user.height && user.weight) {
      setStep('bmi');
    }
  };

  const calculateBMI = () => {
    const heightInMeters = parseFloat(user.height) / 100;
    const bmiValue = (parseFloat(user.weight) / (heightInMeters * heightInMeters)).toFixed(2);

    setBmi(bmiValue);

    if (bmiValue < 18.5) setStatus('Underweight');
    else if (bmiValue < 25) setStatus('Normal');
    else if (bmiValue < 30) setStatus('Overweight');
    else setStatus('Obese');
  };

  const handleInputChange = (field, value) => {
    setUser(prev => ({ ...prev, [field]: value }));
    setError('');
  };

  const logout = () => {
    setStep('signup');
    setUser({ name: '', email: '', password: '', age: '', gender: '', height: '', weight: '' });
    setBmi(null);
    setStatus('');
    setError('');
  };

  // Sign Up Screen
  if (step === 'signup') {
    return (
      <div className="app">
        <div className="container">
          <h1>üìù BMI Calculator - Sign Up</h1>
          <form onSubmit={handleSignup} className="auth-form">
            <div className="form-group">
              <label>Full Name</label>
              <input
                type="text"
                value={user.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="Enter your name"
                required
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={user.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="your@email.com"
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={user.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="Create password"
                required
              />
            </div>
            {error && <p className="error">{error}</p>}
            <button type="submit" className="auth-btn">Create Account</button>
            <p className="switch-link" onClick={() => setStep('login')}>
              Already have account? <span>Sign In</span>
            </p>
          </form>
        </div>
      </div>
    );
  }

  // Sign In Screen
  if (step === 'login') {
    return (
      <div className="app">
        <div className="container">
          <h1>üîê BMI Calculator - Sign In</h1>
          <form onSubmit={handleLogin} className="auth-form">
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={user.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="your@email.com"
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={user.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="Enter password"
                required
              />
            </div>
            {error && <p className="error">{error}</p>}
            <button type="submit" className="auth-btn">Sign In</button>
            <p className="switch-link" onClick={() => setStep('signup')}>
              No account? <span>Sign Up</span>
            </p>
          </form>
        </div>
      </div>
    );
  }

  // Profile Screen
  if (step === 'profile') {
    return (
      <div className="app">
        <div className="container">
          <div className="header">
            <h1>Welcome, {user.name}!</h1>
            <button onClick={logout} className="logout-btn">Logout</button>
          </div>

          <h2>üë§ Complete Profile</h2>
          <form onSubmit={handleProfileSubmit} className="profile-form">
            <div className="form-row">
              <div className="form-group">
                <label>Age</label>
                <input
                  type="number"
                  value={user.age}
                  onChange={(e) => handleInputChange('age', e.target.value)}
                  placeholder="Years"
                  min="1"
                  max="120"
                  required
                />
              </div>
              <div className="form-group">
                <label>Gender</label>
                <select
                  value={user.gender}
                  onChange={(e) => handleInputChange('gender', e.target.value)}
                  required
                >
                  <option value="">Select Gender</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Height (cm)</label>
                <input
                  type="number"
                  value={user.height}
                  onChange={(e) => handleInputChange('height', e.target.value)}
                  placeholder="170"
                  step="0.1"
                  required
                />
              </div>
              <div className="form-group">
                <label>Weight (kg)</label>
                <input
                  type="number"
                  value={user.weight}
                  onChange={(e) => handleInputChange('weight', e.target.value)}
                  placeholder="70"
                  step="0.1"
                  required
                />
              </div>
            </div>

            <button type="submit" className="next-btn">Calculate BMI</button>
          </form>
        </div>
      </div>
    );
  }

  // BMI Result Screen
  return (
    <div className="app">
      <div className="container">
        <div className="header">
          <h1>üìä {user.name}'s BMI Result</h1>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>

        <div className="result-container">
          <div className="user-info">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Age:</strong> {user.age} years</p>
            <p><strong>Gender:</strong> {user.gender}</p>
          </div>

          <div className="bmi-result">
            <h2>Your BMI: <span>{bmi}</span></h2>
            <p className={`status ${status.toLowerCase()}`}>{status}</p>
          </div>

          <div className="actions">
            <button onClick={() => setStep('profile')} className="recalc-btn">
              Recalculate
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
